import 'package:flutter/material.dart';
import 'package:pointycastle/api.dart' as crypto;
import 'package:rsa_encrypt/rsa_encrypt.dart';

class User {
  String id;
  crypto.AsymmetricKeyPair keyPair;
  bool isMember = false;
  bool isAdmin = false;
  String public = "";
  String private = "";

  User({@required this.id, @required this.isMember, @required this.isAdmin}) {
    this.id = id;
    RsaKeyHelper keyHelper = new RsaKeyHelper();
    keyPair = new crypto.AsymmetricKeyPair(keyHelper.parsePublicKeyFromPem(public), keyHelper.parsePrivateKeyFromPem(private));
  }
}